import { useState } from 'react'
import styles from './Input.module.css'
import Button from '../Button/Button'
import InputBox from '../UI/InputBox'
import InputModal from './InputModal'

const Input = ({ AnswerList }) => {
  const [inputText, setInputText] = useState({
    username: '',
    age: '',
  })
  const [modalShow, setModalShow] = useState()

  const inputTextHandler = e => {
    const { value, id } = e.target
    setInputText({
      ...inputText,
      [id]: value,
    })
  }

  const inputSubmitHandler = e => {
    e.preventDefault()
    if (
      inputText.username.trim().length === 0 ||
      inputText.age.trim().length === 0
    ) {
      setInputText({
        username: '',
        age: '',
      })
      setModalShow({
        title: 'Error!!',
        content: 'ìž‘ì„±ëž€ì„ ëª¨ë‘ ì±„ì›Œì£¼ì„¸ìš”...ðŸ˜‚',
      })
      return
    }
    AnswerList(inputText.username, inputText.age)
    setInputText({
      username: '',
      age: '',
    })
  }

  const modalHandler = () => {
    setModalShow(null)
  }

  return (
    <>
      <InputModal
        title={modalShow.title}
        content={modalShow.content}
        modalToggle={modalHandler}
      />
      
      <InputBox className={styles['input-wrap']}>
        <form onSubmit={inputSubmitHandler}>
          <label htmlFor="username">Username</label>
          <input
            id="username"
            type="text"
            value={inputText.username}
            onChange={inputTextHandler}
          />
          <label htmlFor="age">Age(Years)</label>
          <input
            id="age"
            type="text"
            value={inputText.age}
            onChange={inputTextHandler}
          />
          <Button type="submit">Add User</Button>
        </form>
      </InputBox>
    </>
  )
}

export default Input
