import { useState } from 'react'
import styles from './InputAnswer.module.css'

const InputAnswer = ({inputText}) => {
  const [inputAnswerShow, setInputAnswerShow] = useState(false)

  return (
    <div className={inputAnswerShow ? styles['input-answer'] : null}>
      {inputText.username} : {inputText.age}
    </div>
  )
}

export default InputAnswer
