import styles from './Button.module.css'

const Button = ({ inputText, children }) => {
  const [inputAnswerShow, setInputAnswerShow] = useState(false)
  return (
    <div className={inputAnswerShow ? styles['input-answer'] : null}>
      {children}
    </div>
  )
}

export default Button
